    private void butCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butCadastrarActionPerformed
        int id = Integer.parseInt(this.txtID.getText());
        String nomeLivro = this.txtlivros.getText();
        String autor = this.txtautor.getText();
        int estoque = Integer.parseInt(this.txtestoque.getText());
        double valor = Double.parseDouble(this.txtvalor.getText());

        c.Cadastrar(id, nomeLivro, autor, estoque, valor);

        this.txtID.setText("");
        this.txtlivros.setText("");
        this.txtautor.setText("");
        this.txtestoque.setText("");
        this.txtvalor.setText("");
    }//GEN-LAST:event_butCadastrarActionPerformed

