    private void butExibirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butExibirActionPerformed
        String info = c.Exibir(Integer.parseInt(this.txtID.getText()));

        if (!info.equals("")){
            String vetor[] = info.split(";");
            this.txtlivros.setText(vetor[1]);
            this.txtautor.setText(vetor[2]);
            this.txtestoque.setText(vetor[3]);
            this.txtvalor.setText(vetor[4]);
        }else{
            JOptionPane.showMessageDialog(null, "Cadastro não existe!");
            this.txtID.setText("");
        }        
    }//GEN-LAST:event_butExibirActionPerformed

    private void ButAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButAlterarActionPerformed
        c.Alterar(Integer.parseInt(this.txtID.getText()) ,this.txtlivros.getText(), this.txtautor.getText(),Integer.parseInt(this.txtestoque.getText()), Double.parseDouble(this.txtvalor.getText()));

        this.txtID.setText("");
        this.txtlivros.setText("");
        this.txtautor.setText("");
        this.txtestoque.setText("");
        this.txtvalor.setText("");
    }//GEN-LAST:event_ButAlterarActionPerformed

    private void btnListarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnListarActionPerformed
        c.Listar();
    }//GEN-LAST:event_btnListarActionPerformed

    private void butExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butExcluirActionPerformed
        c.Excluir(Integer.parseInt(this.txtID.getText()));
        
        this.txtID.setText("");
        this.txtlivros.setText("");
        this.txtautor.setText("");
        this.txtestoque.setText("");
        this.txtvalor.setText("");
    }//GEN-LAST:event_butExcluirActionPerformed

